from behave import given, when, then
from selenium import webdriver
from selenium.webdriver.common.by import By
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as EC
import time

file_path = "index.html"

@given('the webpage "Library Management System" is loaded')
def step_given_webpage_loaded(context):
    context.driver = webdriver.Chrome()
    context.driver.get(f"file://index.html")
    time.sleep(1)

@given('the window size is small enough to hide the navigation menu')
def step_given_window_size_small(context):
    context.driver.set_window_size(480, 800)
    time.sleep(1)

@given('the navigation menu is initially collapsed')
def step_given_navigation_menu_collapsed(context):
    navbar = context.driver.find_element(By.CSS_SELECTOR, "[data-testid='navbarSupportedContent']")
    assert is_collapsed(navbar), "Expected the navigation menu to be collapsed"

@when('the user clicks the navbar toggler button with data-testid "navbar-toggler"')
def step_when_user_clicks_navbar_toggler(context):
    toggler = WebDriverWait(context.driver, 10).until(
        EC.element_to_be_clickable((By.CSS_SELECTOR, "[data-testid='navbar-toggler']"))
    )
    toggler.click()
    time.sleep(1)

@then('the navigation menu with data-testid "navbarSupportedContent" should be expanded')
def step_then_navigation_menu_expanded(context):
    navbar = context.driver.find_element(By.CSS_SELECTOR, "[data-testid='navbarSupportedContent']")
    assert is_expanded(navbar), "Expected the navigation menu to be expanded"

@then('the menu items should be visible')
def step_then_menu_items_visible(context):
    menu_items = context.driver.find_element(By.CSS_SELECTOR, "[data-testid='nav-link-home']")
    assert menu_items.is_displayed(), "Expected menu items to be visible"

def is_expanded(element):
    aria = element.get_attribute("aria-expanded")
    if aria is not None:
        return aria == "true"

    class_list = element.get_attribute("class").split()
    if any(cls in class_list for cls in ["expanded", "open", "show"]):
        return True

    data_expanded = element.get_attribute("data-expanded")
    if data_expanded is not None:
        return data_expanded == "true"

    return element.is_displayed()

def is_collapsed(element):
    aria = element.get_attribute("aria-expanded")
    if aria is not None and aria.lower() == "false":
        return True

    class_attr = element.get_attribute("class") or ""
    class_list = class_attr.split()
    if "collapsed" in class_list:
        return True

    data_expanded = element.get_attribute("data-expanded")
    if data_expanded is not None and data_expanded.lower() == "false":
        return True

    style = element.get_attribute("style") or ""
    if "display: none" in style or "visibility: hidden" in style or "height: 0" in style:
        return True

    return not element.is_displayed()

def after_scenario(context, scenario):
    context.driver.quit()

