from behave import given, then
from selenium import webdriver
from selenium.webdriver.common.by import By
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as EC
import time

file_path = "index.html"

def is_visible(element):
    return element.is_displayed()

@given('the user navigates to the Online Quiz Portal webpage')
def step_given_user_navigates_to_quiz_portal(context):
    context.driver = webdriver.Chrome()
    context.driver.get(f"file://index.html")
    time.sleep(1)

@then('the system displays the title "Online Quiz Portal"')
def step_then_system_displays_title(context):
    title_element = WebDriverWait(context.driver, 10).until(
        EC.visibility_of_element_located((By.TAG_NAME, "h1"))
    )
    assert "Online Quiz Portal" in title_element.text, f"Expected 'Online Quiz Portal' in '{title_element.text}'"
    time.sleep(1)

@then('the system displays the first question "What is the capital of France?" with its options')
def step_then_system_displays_first_question(context):
    question_element = WebDriverWait(context.driver, 10).until(
        EC.visibility_of_element_located((By.CSS_SELECTOR, "[data-testid='question']"))
    )
    assert "What is the capital of France?" in question_element.text, f"Expected 'What is the capital of France?' in '{question_element.text}'"
    time.sleep(1)

@then('the options displayed are "Berlin", "Madrid", "Paris", "Lisbon"')
def step_then_options_displayed(context):
    options_element = WebDriverWait(context.driver, 10).until(
        EC.visibility_of_element_located((By.CSS_SELECTOR, "[data-testid='options']"))
    )
    options_text = [option.text for option in options_element.find_elements(By.TAG_NAME, "label")]
    expected_options = ["Berlin", "Madrid", "Paris", "Lisbon"]
    for expected_option in expected_options:
        assert expected_option in options_text, f"Expected option '{expected_option}' not found in '{options_text}'"
    time.sleep(1)

def after_scenario(context, scenario):
    context.driver.quit()

