from behave import given, when, then
from selenium import webdriver
from selenium.webdriver.common.by import By
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as EC
import time

file_path = "/Users/lingyao/PycharmProjects/E2ESD_data_construct/data/Ourdatas/LJY_TEST_NEW2/E2ESD_Bench_18/index.html"

# Background: Initialize the browser and load the test page
@given('the "Timetable Generator" page is loaded')
def step_given_timetable_generator_page_is_loaded(context):
    context.driver = webdriver.Chrome()
    context.driver.get(f"file:///Users/lingyao/PycharmProjects/E2ESD_data_construct/data/Ourdatas/LJY_TEST_NEW2/E2ESD_Bench_18/index.html")
    time.sleep(1)  # Allow the page to load completely

@given('the "Subject" input field with data-testid "subject" is visible')
def step_given_subject_input_field_is_visible(context):
    WebDriverWait(context.driver, 10).until(
        EC.visibility_of_element_located((By.CSS_SELECTOR, "[data-testid='subject']"))
    )
    subject_field = context.driver.find_element(By.CSS_SELECTOR, "[data-testid='subject']")
    assert subject_field.is_displayed(), "Subject input field is not visible"

@when('the user enters "Mathematics" into the "Subject" input field with data-testid "subject"')
def step_when_user_enters_subject(context):
    subject_field = context.driver.find_element(By.CSS_SELECTOR, "[data-testid='subject']")
    subject_field.clear()  # Clear any existing text
    subject_field.send_keys("Mathematics")
    time.sleep(1)  # Allow time for the input action to complete

@then('the "Subject" input field with data-testid "subject" should display the value "Mathematics"')
def step_then_subject_field_should_display_value(context):
    subject_field = context.driver.find_element(By.CSS_SELECTOR, "[data-testid='subject']")
    displayed_value = subject_field.get_attribute("value")
    assert displayed_value == "Mathematics", f"Expected 'Mathematics', but got '{displayed_value}'"

# Cleanup: Close the browser after the test
def after_scenario(context, scenario):
    if hasattr(context, 'driver'):
        context.driver.quit()

