from selenium import webdriver
from selenium.webdriver.common.by import By
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as EC
import time

file_path = "/Users/lingyao/PycharmProjects/E2ESD_data_construct/data/Ourdatas/LJY_TEST_NEW2/E2ESD_Bench_18/index.html"

def is_checkbox_checked(driver, checkbox_selector):
    checkbox = driver.find_element(By.CSS_SELECTOR, checkbox_selector)
    return checkbox.is_selected()

@given('the "Timetable Generator" page is loaded')
def step_given_timetable_generator_page_loaded(context):
    context.driver = webdriver.Chrome()
    context.driver.get(f"file:///Users/lingyao/PycharmProjects/E2ESD_data_construct/data/Ourdatas/LJY_TEST_NEW2/E2ESD_Bench_18/index.html")
    time.sleep(1)  # Allow time for the page to load

@given('the "Days" checkbox group with data-testid "days" is visible')
def step_given_days_checkbox_group_visible(context):
    WebDriverWait(context.driver, 10).until(
        EC.visibility_of_element_located((By.CSS_SELECTOR, "[data-testid='days']"))
    )
    time.sleep(1)  # Ensure visibility

@given('the checkbox labeled "Monday" with data-testid "day-monday" is already checked')
def step_given_checkbox_monday_checked(context):
    checkbox = WebDriverWait(context.driver, 10).until(
        EC.presence_of_element_located((By.CSS_SELECTOR, "[data-testid='day-monday'] input[type='checkbox']"))
    )
    if not checkbox.is_selected():
        checkbox.click()
        time.sleep(1)  # Allow time for the interaction

@when('the user unchecks the checkbox labeled "Monday" with data-testid "day-monday"')
def step_when_user_unchecks_checkbox_monday(context):
    checkbox = WebDriverWait(context.driver, 10).until(
        EC.element_to_be_clickable((By.CSS_SELECTOR, "[data-testid='day-monday'] input[type='checkbox']"))
    )
    if checkbox.is_selected():
        checkbox.click()
        time.sleep(1)  # Allow time for the interaction

@then('the checkbox labeled "Monday" should no longer be marked as checked')
def step_then_checkbox_monday_not_checked(context):
    checkbox_selector = "[data-testid='day-monday'] input[type='checkbox']"
    is_checked = is_checkbox_checked(context.driver, checkbox_selector)
    assert not is_checked, "Expected the checkbox labeled 'Monday' to be unchecked, but it is still checked."

# Cleanup after tests
def after_scenario(context, scenario):
    if hasattr(context, 'driver'):
        context.driver.quit()

