        EC.presence_of_element_located((By.CSS_SELECTOR, "[data-testid='timetable'] tbody"))
    )
    rows = timetable_body.find_elements(By.TAG_NAME, "tr")
    assert len(rows) > 0, "No rows were added to the timetable table"

@then('the row contains "{time_range}" in the first column')
def step_impl(context, time_range):
    timetable_body = context.driver.find_element(By.CSS_SELECTOR, "[data-testid='timetable'] tbody")
    first_row = timetable_body.find_elements(By.TAG_NAME, "tr")[0]
    first_column = first_row.find_elements(By.TAG_NAME, "td")[0]
    assert time_range in first_column.text, f"Expected '{time_range}' in the first column, but got '{first_column.text}'"

@then('the row contains "{subject}" under the "{day}" column')
def step_impl(context, subject, day):
    timetable_body = context.driver.find_element(By.CSS_SELECTOR, "[data-testid='timetable'] tbody")
    first_row = timetable_body.find_elements(By.TAG_NAME, "tr")[0]
    day_index = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"].index(day) + 1
    day_column = first_row.find_elements(By.TAG_NAME, "td")[day_index]
    assert subject in day_column.text, f"Expected '{subject}' under the '{day}' column, but got '{day_column.text}'"


# Teardown
def after_scenario(context, scenario):
    context.driver.quit()

