from behave import given, when, then
from selenium import webdriver
from selenium.webdriver.common.by import By
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as EC
import time

file_path = "index.html"

@given('the user opens the Expense Tracker application')
def step_given_user_opens_application(context):
    context.driver = webdriver.Chrome()
    context.driver.get(f"file://index.html")
    time.sleep(1)

@when('the application is loaded')
def step_when_application_is_loaded(context):
    # Wait for the balance element to be visible to ensure the page is loaded
    WebDriverWait(context.driver, 10).until(
        EC.visibility_of_element_located((By.CSS_SELECTOR, "[data-testid='balance']"))
    )
    time.sleep(1)

@then('the balance displayed with data-testid "balance" should be "$0.00"')
def step_then_balance_should_be_zero(context):
    balance_element = context.driver.find_element(By.CSS_SELECTOR, "[data-testid='balance']")
    assert balance_element.text.strip() == "$0.00", f"Expected balance to be '$0.00', but got '{balance_element.text}'"
    time.sleep(1)

@then('the income displayed with data-testid "income" should be "$0.00"')
def step_then_income_should_be_zero(context):
    income_element = context.driver.find_element(By.CSS_SELECTOR, "[data-testid='income']")
    assert income_element.text.strip() == "$0.00", f"Expected income to be '$0.00', but got '{income_element.text}'"
    time.sleep(1)

@then('the expenses displayed with data-testid "expense" should be "$0.00"')
def step_then_expenses_should_be_zero(context):
    expense_element = context.driver.find_element(By.CSS_SELECTOR, "[data-testid='expense']")
    assert expense_element.text.strip() == "$0.00", f"Expected expenses to be '$0.00', but got '{expense_element.text}'"
    time.sleep(1)

def after_scenario(context, scenario):
    context.driver.quit()

