from behave import given, when, then
from selenium import webdriver
from selenium.webdriver.common.by import By
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as EC
import time

file_path = "index.html"

@given('the Expense Tracker page is loaded')
def step_given_expense_tracker_page_loaded(context):
    context.driver = webdriver.Chrome()
    context.driver.get(f"file://index.html")
    time.sleep(1)

@given('the \'Description\' input field with data-testid \'description-input\' is visible')
def step_given_description_input_visible(context):
    description_input = WebDriverWait(context.driver, 10).until(
        EC.visibility_of_element_located((By.CSS_SELECTOR, "[data-testid='description-input']"))
    )
    assert description_input.is_displayed(), "Description input field is not visible"
    time.sleep(1)

@when('the user enters "Grocery Shopping" into the \'Description\' input field with data-testid \'description-input\'')
def step_when_user_enters_description(context):
    description_input = context.driver.find_element(By.CSS_SELECTOR, "[data-testid='description-input']")
    description_input.clear()
    description_input.send_keys("Grocery Shopping")
    time.sleep(1)

@when('the user enters "50" into the \'Amount\' input field with data-testid \'amount-input\'')
def step_when_user_enters_amount(context):
    amount_input = context.driver.find_element(By.CSS_SELECTOR, "[data-testid='amount-input']")
    amount_input.clear()
    amount_input.send_keys("50")
    time.sleep(1)

@when('the user clicks the \'Add Transaction\' button with data-testid \'add-transaction-button\'')
def step_when_user_clicks_add_transaction(context):
    add_transaction_button = WebDriverWait(context.driver, 10).until(
        EC.element_to_be_clickable((By.CSS_SELECTOR, "[data-testid='add-transaction-button']"))
    )
    add_transaction_button.click()
    time.sleep(1)

@then('the transaction history with data-testid \'transaction-list\' should display a row with the description "Grocery Shopping"')
def step_then_transaction_history_displays_description(context):
    transaction_list = WebDriverWait(context.driver, 10).until(
        EC.visibility_of_element_located((By.CSS_SELECTOR, "[data-testid='transaction-list']"))
    )
    rows = transaction_list.find_elements(By.TAG_NAME, "tr")
    assert any("Grocery Shopping" in row.text for row in rows), "Transaction description 'Grocery Shopping' not found in history"
    time.sleep(1)

def after_scenario(context, scenario):
    context.driver.quit()

