    description_input = WebDriverWait(context.driver, 10).until(
        EC.presence_of_element_located((By.CSS_SELECTOR, "[data-testid='description-input']"))
    )
    assert description_input.get_attribute('value') == "", "Description input is not empty"
    time.sleep(1)

@given('the amount input with data-testid "amount-input" is empty')
def step_given_amount_input_empty(context):
    amount_input = WebDriverWait(context.driver, 10).until(
        EC.presence_of_element_located((By.CSS_SELECTOR, "[data-testid='amount-input']"))
    )
