        EC.element_to_be_clickable((By.CSS_SELECTOR, "[data-testid='add-transaction-button']"))
    )
    add_transaction_button.click()
    time.sleep(1)

@when('the user reloads the page')
def step_when_user_reloads_page(context):
    context.driver.refresh()
    time.sleep(1)

@then('the transaction list with data-testid "transaction-list" should contain a transaction with description "{description}" and amount "{amount}"')
def step_then_transaction_list_contains(context, description, amount):
    transaction_list = WebDriverWait(context.driver, 10).until(
        EC.visibility_of_element_located((By.CSS_SELECTOR, "[data-testid='transaction-list']"))
    )
    transactions = transaction_list.find_elements(By.TAG_NAME, "tr")
    found = False
    for transaction in transactions:
        cells = transaction.find_elements(By.TAG_NAME, "td")
        if len(cells) >= 3:
            transaction_description = cells[1].text.strip()
            transaction_amount = cells[2].text.strip()
            if description.lower() in transaction_description.lower() and amount.lower() in transaction_amount.lower():
                found = True
                break
    assert found, f"Transaction with description '{description}' and amount '{amount}' not found."

@then('the local storage should contain the transaction with description "{description}" and amount "{amount}"')
def step_then_local_storage_contains(context, description, amount):
    transactions = context.driver.execute_script("return JSON.parse(localStorage.getItem('transactions')) || [];")
    found = False
    for transaction in transactions:
        if transaction['text'].lower() == description.lower() and str(transaction['amount']) == amount:
            found = True
            break
    assert found, f"Local storage does not contain transaction with description '{description}' and amount '{amount}'."

def after_scenario(context, scenario):
    context.driver.quit()

